	fmt.Println("自定义日志中间件第二种方式-----请求之前")
	//在gin上下文定义一个变量
	c.Set("example", "12345")
	//请求之前
	c.Next()
	fmt.Println("自定义日志中间件第二种方式-----请求之后")
	//计算整个请求过程
	latency := time.Since(t)
	log.Print(latency)

	//请求状态打印
	status := c.Writer.Status()
	log.Println(status)
}
func main() {
	r := gin.New()
	r.Use(Logger())
	//r.Use(Logger1)
	r.GET("/test", func(ctx *gin.Context) {
		exam := ctx.MustGet("example").(string)
		log.Println(exam)
	})
	r.Run()
}

